-- Deploy cbibs_db:standard_name_verification to pg

BEGIN;

SET search_path = cbibs, pg_catalog;

SET default_tablespace = '';

SET default_with_oids = false;

--
-- Name: cf_standard_names; Type: TABLE; Schema: public; Owner: postgres; Tablespace: 
--

CREATE TABLE cf_standard_names (
    id serial PRIMARY KEY,
    standard_name text NOT NULL,
    amip text,
    canonical_units text,
    description text,
    grib text
);


ALTER TABLE cf_standard_names OWNER TO cbibsroot;

--
-- Name: TABLE cf_standard_names; Type: COMMENT; Schema: public; Owner: postgres
--

COMMENT ON TABLE cf_standard_names IS 'CF standard name table, version 28.  Generated from http://cfconventions.org/Data/cf-standard-names/29/src/cf-standard-name-table.xml';


--
-- Name: cf_standard_names_id_seq; Type: SEQUENCE OWNED BY; Schema: public; Owner: postgres
--

ALTER SEQUENCE cf_standard_names_id_seq OWNED BY cf_standard_names.id;


--
-- Name: id; Type: DEFAULT; Schema: public; Owner: postgres
--

ALTER TABLE ONLY cf_standard_names ALTER COLUMN id SET DEFAULT nextval('cf_standard_names_id_seq'::regclass);


--
-- Data for Name: cf_standard_names; Type: TABLE DATA; Schema: public; Owner: postgres
--



--
-- Name: cf_standard_names_id_seq; Type: SEQUENCE SET; Schema: public; Owner: postgres
--

SELECT pg_catalog.setval('cf_standard_names_id_seq', 2672, true);


--
-- Name: cf_standard_names_standard_name_key; Type: CONSTRAINT; Schema: public; Owner: postgres; Tablespace: 
--

ALTER TABLE ONLY cf_standard_names
    ADD CONSTRAINT cf_standard_names_standard_name_key UNIQUE (standard_name);


