--this could almost certainly be done in a better way
UPDATE d_variable SET netcdf_name = NULL;
-- needs to be split out in postgres prior to 9.5??
UPDATE d_variable
    SET netcdf_name = CASE actual_name
        WHEN 'current_average_direction' THEN 'direction_of_sea_water_velocity'
        WHEN 'current_average_velocity' THEN 'sea_water_speed'
        WHEN 'current_velocity' THEN 'sea_water_speed'
        WHEN 'sea_surface_wave_mean_height' THEN 'sea_surface_wave_significant_height'
        WHEN 'sea_surface_wave_mean_height' THEN 'sea_surface_wave_significant_height'
        WHEN 'simple_turbidity' THEN 'sea_water_turbidity'
        -- WHEN 'wave_direction_spread' THEN NULL  -- redundant
        ELSE NULL
    END;
-- for the remaining names, join against the standard name table
UPDATE d_variable v SET netcdf_name = s.standard_name 
    FROM cf_standard_names s WHERE s.standard_name = v.actual_name; 
UPDATE d_variable
    SET depth_cell_method = CASE actual_name
        WHEN 'current_average_direction' THEN 'mean'
        WHEN 'current_average_velocity' THEN 'mean'
        WHEN 'current_velocity' THEN NULL
        WHEN 'sea_surface_wave_mean_height' THEN NULL
        WHEN 'sea_surface_wave_mean_height' THEN NULL
        WHEN 'simple_turbidity' THEN NULL
        WHEN 'wave_direction_spread' THEN NULL
        ELSE NULL
    END;
UPDATE d_variable
    SET time_cell_method = CASE actual_name
        WHEN 'current_average_direction' THEN NULL
        WHEN 'current_average_velocity' THEN NULL
        WHEN 'current_velocity' THEN NULL
        WHEN 'sea_surface_wave_mean_height' THEN 'mean'
        WHEN 'sea_surface_wave_mean_height' THEN 'mean'
        WHEN 'simple_turbidity' THEN NULL
        WHEN 'wave_direction_spread' THEN NULL
        ELSE NULL
    END;
